INSERT INTO `halaman` (`id`, `judul`, `konten`, `slug`, `created_at`, `updated_at`) VALUES
(1, 'Sambutan Ketua LPPM', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Assalamualaikum Wr. Wb..</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Salam sejahtera bagi kita semua..</span></span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Om </span></span></em><em><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Swastyastu..</span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Puji syukur kami panjatkan kehadirat Allah SWT atas limpahan Rahmat dan Hidayah-Nya sehingga Lembaga Penelitian dan Pengabdian Masyarakat (LPPM) dapat berkontribusi dalam peningkatan kualitas tri dharma di Institut Teknologi Kalimantan. ITK terus berupaya membangun diri menjadi perguruan tinggi yang unggul dan mampu berperan aktif dalam pembangunan nasional melalui penelitian dan pengabdian masyarakat yang maju dan berkelanjutan. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Lembaga Penelitian dan Pengabdian Kepada Masyarakar didirikan melalui SK Rektor ITK No. 0021/ITK/HK.KP/2015 tanggal 6 Januari 2015. LPPM ITK berperan dalam mengelola dan mengembangkan topik-topik penelitian dan pengabdian masyarakat melalui pendanaan Hibah Internal ITK dan Hibah Eksternal. Berkaitan dengan tujuan tersebut telah disusun Rencana Strategis (Renstra) Penelitian dan Pengabdian Masyarakat sebagai dokumen formal yang berisi strategi pencapaian serta topik-topik penelitian unggulan institusi termasuk topik-topik riset yang harus diacu oleh peneliti di dalam melakukan penelitian dan pengabdian masyarakat pada masa 5 (lima) tahun kedepan. &nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Selanjutnya dalam rangka mengembangkan riset yang berdaya saing global, ITK telah membentuk fokus riset unggulan yang terdiri dari Energi, Pangan-Pertanian, Kemaritiman dan Smart City. Keempat fokus riset tersebut terus diupayakan dalam pengelolaannya mulai dari riset dasar, pengembangan dan terapan. Untuk mendorong hal tersebut maka banyak skema kerja sama riset dilakukan dengan berbagai pihak industri, lembaga penelitian, dan perguruan tinggi baik di dalam maupun luar negeri. Hasil hasil penelitian diharapkan dapat memperoleh Hak Kekayaan Intelektual dan berhasil diterapkan dimasyarakat dan mendapatkan rekognisi internasional. Hingga saat ini capaian hasil riset yang berhasil dipublikasikan baik nasional maupun internasional selalu mengalami peningkatan dari tahun ketahun. Publikasi nasional mengalami peningkatan dari tahun 2019 sebesar 51 judul menjadi 68 judul pada tahun 2020, sedangkan publikasi internasional terjadi peningkatan dari 34 judul menjadi 48 judul. Selain itu hingga tahun 2020 sudah ada 11 judul HKI yang telah didaftarkan. Tentu capaian tersebut berkat kerja keras dari bapak ibu dosen, mahasiswa dan semua pihak yang terlibat dalam mendukung pelaksanaan riset di laboratorium maupun pencapaian luarannya. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">LPPM ITK selalu siap mendukung program Merdeka Belajar Kampus Merdeka (MBKM) dengan terus berupaya meningkatkan partisipasi dosen dan mahasiswa yang terlibat dalam penelitian dan pengabdian masyarakat. Banyak skema riset dan pengabdian yang disediakan oleh LPPM ITK dalam rangka mensukseskan pelaksanaan MBKM untuk pencapaian Indikator Kinerja Utama (IKU) hingga pengembangan dan hilirisasi produk luarannya. Semoga riset-riset di ITK dapat semakin berkembang dengan peningkatan kualitas dan kuantitasnya dalam mendukung pelaksanaan pembangunan nasional yang berkelanjutan. Aamiin.. </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Wassalamualaikum Wr Wb.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Salam..</span></span></em></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Om shanti shanti.. &nbsp;</span></span></em></span></span></p>', 'sambutan-ketua-lppm', '2020-08-26 18:22:09', '2021-06-01 18:37:12'),
(2, 'Sejarah', '<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; Lembaga Penelitian dan Pengabdian Kepada Masyarakar (LPPM) Institut Teknologi Kalimantan (ITK) didirikan melalui SK Rektor ITK No. 6 0021/ITK/HK.KP/2015 tanggal 6 Januari 2015. LPPM ITK didirikan dalam upaya meningkatkan penelitian dan kewajiban melaksanakan Tri Dharma Perguruan Tinggi yaitu tiga pilar dasar pola pikir yang menjadi kewajiban bagi staf pengajar, yang meliputi : Pendidikan, Penelitian dan Pengabdian kepada Masyarakat. Pendirian ini didasarkan pada OTK, Statuta, dan Rencana Strategis ITK serta visi dan misi ITK. Pengambilan keputusan dalam pengelolaan penelitian di lingkungan ITK dilakukan oleh Rektor ITK. Lembaga Penelitian dan Pengabdian Kepada Masyarakat (LPPM) Institut Teknologi Kalimantan adalah pelaksana keputusan Rektor ITK di Bidang penelitian dan pengabdian kepada masyarakat. Dalam melaksanakan tugas, pokok dan fungsi lembaga Ketua LPPM, dibantu oleh satu orang sekretaris dan staf administrasi serta 3 kepala pusat, diantaranya kepala pusat penelitian dan publikasi ilmiah, kepala pusat pengabdian masyarakat dan HKI, dan kepala pusat kerjasama dan inovasi. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; ITK dengan bermodalkan SDM yang berkualitas melakukan penelitian dan pengabdian masyarakat dimana bidang penelitian dan pengabdian masyarakat berada dibawah koordinasi Wakil Rektor Bidang Akademik. Maksud pembentukan bidang penelitian dan pengabdian masyarakat antara lain:</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">a. Melaksanakan Tri Dharma Perguruan Tinggi</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">b. Mengembangkan potensi daerah baik Sumber Daya Alam (SDA) maupun Sumber Daya Manusia (SDM). </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">c. Menyelesaikan permasalahan masyarakat dalam bidang sains dan teknologi. </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">d. Menemukan dan mematenkan inovasi baru dalam bidang sains dan teknologi. </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; Sedangkan tujuan pembentukan bidang penelitian dan pengabdian masyarakat antara lain: </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">a. Meningkatkan kuantitas dan kualitas penelitian dan pengabdian masyarakat. </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">b. Membantu proses hilirisasi penelitian. </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">c. Mengembangkan sikap dan perilaku SDM dalam penelitian dan pengabdian kepada masyarakat. </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">d. Mengembangkan manajemen Penelitian dan Pengabdian kepada Masyarakat dalam struktur organisasi dan manajemen yang sehat </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; Secara ringkas, tugas utama LPPM bisa dikategorikan ke dalam tiga kelompok : </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>1. Pengembangan sistem</strong> : menyangkut kebijakan, prosedur dan jaminan mutu penelitian dan pengabdian masyarakat </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>2. Peningkatan kapasitas&nbsp;</strong>: menyangkut kemampuan penelitian, pengabdian dan publikasi baik di tingkat internasional, nasional terakreditasi </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>3. Peningkatan koordinasi dan kerja sama </strong>: baik kerja sama internal maupun eksternal. Secara internal menyangkut sinkronisasi kebijakan dan kerja dalam melakukan kegiatan penelitian dan pengabdian masyarakat antar unit di lingkungan ITK. Sementara koordinasi dan kerja sama dengan pihak eksternal menyangkut jejaring kerja (networking), baik dengan sesama institusi penelitian, donor, pemerintah (dikti) dan juga media.</span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; &nbsp; Sesuai visi misi dan sasaran ITK maka orientasi kegiatan LPPM adalah: </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>1. Penelitian </strong>: Penelitian Mandiri dengan dana Institusi, Penelitian lintas unit/lintas Institusi, Penelitian kompetitif, penelitian di Pusat </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>2. Pengabdian pada Masyarakat </strong>: kegiatan pengabdian yang dilakukan oleh Pusat dan Institusi </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>3. Seminar, pelatihan</strong> : seminar/pelatihan yang dilakukan oleh LPPM beserta pusat </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>4. Workshop</strong> yang ditujukan untuk peningkatan kapasitas dosen dan melaksanakan Tri Dharma Perguruan Tinggi </span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Siklus Penelitian </span></span></strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/siklus_1615946080.PNG\" style=\"height:269px; width:350px\" /></span></span></strong></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pada dasarnya, kegiatan-kegiatan LPPM mengacu pada siklus penelitian publikasi, disamping menerjemahkan tugas pokok dalam hal peningkatan kapasitas, koordinasi dan penyusun kebijakan dalam rangka meningkatkan kualitas penelitian dan pengabdian masyarakat.</span></span></p>', 'sejarah', '2020-08-18 04:07:20', '2021-03-16 17:55:40'),
(3, 'Visi, Misi & Tujuan', '<h4 style=\"text-align:justify\" class=\"p-title-isi\">VISI</h4>\r\n\r\n<p style=\"text-align:justify\">Visi lembaga penelitian dan pengabdian masyarakat ITK adalah&nbsp;<em>&ldquo;Menjadi lembaga penelitian dan pengabdian kepada masyarakat yang unggul, terpercaya dalam bidang IPTEK serta mampu berperan aktif dalam pembangunan daerah&rdquo;</em>.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h4 style=\"text-align:justify\" class=\"p-title-isi\">MISI</h4>\r\n\r\n<ol>\r\n	<li style=\"text-align: justify;\">Mengembangkan program penelitian IPTEK yang dapat diterapkan sesuai kebutuhan nyata masyarakat.</li>\r\n	<li style=\"text-align: justify;\">Mewujudkan program pengabdian kepada masyarakat melalui hasil penelitian unggulan untuk meningkatkan kesejahteraan masyarakat.</li>\r\n	<li style=\"text-align: justify;\">Mengarahkan, mengembangkan, dan menjalin kerja sama baik internal maupun eksternal untuk meningkatkan kualitas dan pendanaan penelitian dan pengabdian kepada masyarakat.</li>\r\n	<li style=\"text-align: justify;\">Meningkatkan kemandirian lembaga dan pusat studi dalam program penelitian dan pengabdian kepada masyarakat.</li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h4 style=\"text-align:justify\" class=\"p-title-isi\">TUJUAN</h4>\r\n\r\n<ol>\r\n	<li style=\"text-align: justify;\">Meningkatkan kuantitas dan kualitas penelitian dan pengabdian masyarakat.</li>\r\n	<li style=\"text-align: justify;\">Membantu proses hilirisasi penelitian.</li>\r\n	<li style=\"text-align: justify;\">Mengembangkan sikap dan perilaku SDM dalam penelitian dan pengabdian kepada masyarakat.</li>\r\n	<li style=\"text-align: justify;\">Mengembangkan manajemen Penelitian dan Pengabdian kepada Masyarakat dalam struktur organisasi dan manajemen yang sehat.</li>\r\n</ol>', 'visi-misi-tujuan', '2020-08-18 04:07:58', '2020-09-09 07:29:46'),
(4, 'Struktur Organisasi', '<p>&nbsp;</p>\r\n\r\n<p>.</p>', 'struktur-organisasi', '2020-08-18 04:12:30', '2021-06-09 19:15:43'),
(6, 'Pusat Penelitian dan Publikasi Ilmiah', '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Penelitian merupakan salah satu tugas utama dosen yang harus dilakukan dalam upaya melaksanakan Tri Dharma Perguruan Tinggi yaitu tiga pilar dasar pola pikir yang menjadi kewajiban bagi staf pengajar, yang meliputi: Pendidikan, Penelitian dan Pengabdian kepada Masyarakat. Sebagai upaya untuk meningkatkan jumlah penelitian dan publikasi yang dilakukan oleh dosen, maka ITK melalui Lembaga Peneltian dan Pengabdian kepada Masyarakat (LPPM) terus mendorong dosen melakukan penelitian dan menghasilkan inovasi-inovasi baru mengikuti perkembangan jaman, khususnya dalam menyambut tantangan revolusi industri 4.0.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kegiatan penelitian yang berjalan dengan baik akan menghasilkan data informasi hasil penelitian yang maju dan Gedung Riset dan Pusat Unggulan berpotensi untuk publikasi nasional maupun internasional. Kegiatan publikasi ini sangat mendukung percepatan pengembangan ITK dalam bidang keilmuan untuk menjadi kampus unggul dalam riset dan aplikasinya. Kegiatan penelitian akan berjalan dengan baik dan maju jika tersedia sarana dan prasarana yang memadai. Oleh karena itu pengembangan ITK kedepan direncanakan untuk membangun <em>Gedung Research Centre</em> yang menjadi pusat kegiatan penelitian yang dilengkapi dengan peralatan yang advanced. Selain itu pembangunan gedung laboratorium merupakan upaya untuk meningkatkan pelayanan praktikum, peningkatan keahlian mahasiswa yang merupakan aspek yang penting untuk segera dipenuhi. Terpenuhinya sarana dan prasarana tersebut diharapkan dapat mendukung proses perkuliahan secara efektif dan efisien. Oleh karena itu pada kesempatan ini diusulkan penyusunan Rencana Strategis Penelitian sebagai arah pengembangan penelitian di Institut Teknologi Kalimantan.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Berdasarkan surat keputusan Dirjen Penguatan Risbang nomor B/5678/E1.2/H.M.00.03/2019 tanggal 13 November 2019 tentang Klaster atau pengelompokkan Perguruan Tinggi berbasis penelitian periode tahun 2016 &ndash; 2018, ITK berhasil naik peringkat dari klister Mandiri ke klaster Madya. Peningkatan kualitas penelitian dengan memperhatikan mutu yang ada akan terus dilakukan oleh LPPM ITK demi terwujudnya penelitian berbasis luaran.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sesuai visi misi dan sasaran ITK maka orientasi kegiatan LPPM di bidang penelitian dan publikasi ilmiah adalah:</span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Penelitian: Penelitian Mandiri dengan dana Institusi, Penelitian lintas unit/lintas Institusi, Penelitian kompetitif, penelitian di Pusat.</span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Seminar, pelatihan: seminar/pelatihan yang dilakukan oleh LPPM beserta pusat. </span></span></li>\r\n	<li><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Workshop yang ditujukan untuk peningkatan kapasitas dosen dan melaksanakan Tri Dharma Perguruan Tinggi.</span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/siklus penelitian_1612320054.png\" style=\"height:391px; width:392px\" /></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Pada dasarnya, kegiatan-kegiatan LPPM mengacu pada siklus penelitian publikasi, disamping menerjemahkan tugas pokok dalam hal peningkatan kapasitas, koordinasi dan penyusun kebijakan dalam rangka meningkatkan kualitas penelitian dan pengabdian masyarakat.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Adapun Tugas Pokok dan Fungsi dari Koordinator Pusat Penelitian dan Publikasi yaitu:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mengkoordinir pelaksanaan dan pengembangan penelitian di ITK.</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Membantu meningkatkan mutu penelitian dengan mengadakan pelatihan atau kegiatan ilmiah untuk diseminasi dan pembahasan hasil penelitian.</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Membantu Ketua LPPM dalam menyusun rencana induk penelitian yang disesuaikan dengan perkembangan internal dan eksternal perguruan tinggi, perkembangan IPTEK dan permasalahan masyarakat.</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Membantu Ketua LPPM dalam menyusun program kerja bidang penelitian</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Membantu Ketua LPPM dalam penyusunan capaian kinerja triwulan dan&nbsp; laporan tahunan kegiatan LPPM;</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Membantu memberikan solusi atas permasalahan dalam pelaksanaan penelitian di ITK</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Melaksanakan proses review terhadap usulan penelitian, pelaksanaan dan evaluasi akhir</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Membantu kerjasama antar dosen dan antar unit/jurusan untuk membentuk kelompok riset (<em>research group</em>) dalam rangka pelaksanaan dan pengembangan penelitian</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Memfasilitasi peluang penelitian yang bersumber dari pendanaan internal maupun eksternal baik dalam maupun luar negeri</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Melakukan pemantauan dan pendataan penelitian dan publikasi hasil penelitian setiap tiga bulan dan capaian tahunan</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Mengelola proses pelaksanaan dan sistem pengembangan SPECTA Journal of Technology dan Jurnal PIKAT. </span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Melakukan pemantauan terhadap pelaksanaan kegiatan dan publikasi ilmiah di ITK</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Melaksanakan program pengembangan kebijakan insentif.</span></span></li>\r\n</ol>', 'pusat-penelitian-dan-publikasi-ilmiah', '2020-08-26 18:31:59', '2021-02-02 18:42:52'),
(7, 'Pusat Kerjasama dan Pengabdian kepada Masyarakat', '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Kegiatan pengabdian masyarakat dalam kurun waktu 2015 &ndash; 2018, ITK memberikan dana pengabdian untuk 42 tim pengabdi yang terdiri dari 2 tim pengabdi tahun 2015, pada tahun 2016 ada 5 tim pengabdi, tahun 2017 ada 10 tim pengabdi dan pada tahun 2018 sebanyak 15 tim pengabdi. Sedangkan pengabdian masyarakat yang bersumber dari dana pusat ada 4 tim yang terdiri dari 2 tim ditahun 2017 dan 2 tim ditahun 2018, selanjutnya untuk tahun 2019 ada 6 judul yang diusulkan melalui simlitabmas. Output dari tim pengabdi adalah publikasi ilmiah, baik dalam bentuk jurnal maupun seminar (nasional maupun internasional). </span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Selain dari pada itu, dalam rangka meningkatkan jalinan kerjasama, ITK membentuk tim kerja yang khusus menangani bidang kerjasama yang berada dalam koordinasi LPPM ITK. Kerjasama yang dilakukan ITK adalah kerjasama dengan pemerintah kota dan daerah, industri, lembaga pemerintah dan laboratorium serta perguruan tinggi lainnya, baik dalam negeri maupun luar negri. Hingga saat ini setidaknya ada 34 lembaga delam negri dan 4 lembaga luar negri yang berkerjasama dengan ITK dalam berbagai kegiatan. ITK memiliki kerjasama dengan Prancis, NCU, NCKU, UTM Malaysia dan Saga University Jepang untuk pengiriman mahasiswa maupun kerjasama riset.</span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#201449; font-family:Calibri\"><span style=\"font-size:17.3333px\"><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/Picture2_1612419996.png\" style=\"float:left; height:316px; width:717px\" /></span></span></p>', 'pusat-kerjasama-dan-pengabdian-kepada-masyarakat', '2020-08-26 18:32:23', '2021-06-27 18:19:16'),
(8, 'Inkubator Bisnis Teknologi', '<h4><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Pendirian Inkubator Bisnis Teknologi (IBT) ITK diharapkan mampu berperan aktif dalam mendorong peningkatan perusahaan pemula (<em>startup</em>) khususnya berbasis teknologi. IBT - ITK berfungsi melakukan inkubasi perusahaan pemula <em>startup</em> yang lahir baik dari ITK, perguruan tinggi di Kalimantan Timur maupun masyarakat umum. IBT - ITK merupakan IBT pertama di Kalimantan Timur.</span></span></h4>\r\n\r\n<h2><span style=\"font-size:14px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Visi</span></strong></span></h2>\r\n\r\n<p style=\"margin-left:36px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Inkubator Bisnis Teknologi Institut Teknologi Kalimantan (IBT - ITK) mempunyai visi &ldquo;Menjadi pusat inkubator bisnis teknologi bidang pangan terkemuka Kalimantan yang berperan aktif dalam mendorong peningkatan perusahaan pemula berbasis teknologi tahun 2024&rdquo;.</span></span></p>\r\n\r\n<h3><span style=\"font-size:14px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Misi</span></strong></span></h3>\r\n\r\n<p style=\"margin-left:36px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dalam rangka mencapai visi tersebut di atas, misi IBT - ITK adalah sebagai berikut:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">1. Mengembangkan potensi komersialisiasi riset perguruan tinggi.</span></span></li>\r\n	<li style=\"list-style-type:none\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">2. Meningkatkan startup di bidang pangan di Kalimantan dengan teknologi yang merupakan hasil inovasi dari penelitian.</span></span></li>\r\n	<li style=\"list-style-type:none\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Mengembangkan jaringan kerjasama stakeholder terkait dalam menumbuhkan dan mengembangkan perusahaan pemula (startup) berbasis teknologi.</span></span></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Inkubator Bisnis Teknologi (IBT) Institut Teknologi Kalimantan (ITK) secara kelembagaan berada dibawah Lembaga Penelitian dan Pengabdian kepada Masyarakat (LPPM) ITK.&nbsp;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>LEGALITAS KELEMBAGAAN</strong></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">SK Pendirian :SK Rektor ITK Nomor 239/IT10.R/OT.07/2019 Tanggal: 25 Februari 2019<br />\r\nSK Pengelola :SK Rektor ITK Nomor 276/IT10.R/KP.11/2019&nbsp;Tanggal : 1 Maret 2019</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Struktur Organisasi IBT</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/2_1613701267.png\" style=\"height:154px; width:400px\" /></span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>SUMBER DAYA &ndash; SARANA/PRA-SARANA</strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/Picture1_1612419441.png\" style=\"height:452px; width:470px\" /></strong></span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ruang IBT ITK Gedung E Kampus ITK,&nbsp;Ruang Pengelola, Toilet, dan Ruang Tamu IBT ITK, Ruang Tenant Inwall IBT ITK Gedung E.103 Kampus ITK</span></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:14px\"><strong><span style=\"font-family:Arial,Helvetica,sans-serif\">Dokumentasi Kegiatan</span></strong></span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-size:14px\"><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/3_1613701651.PNG\" style=\"height:240px; width:320px\" /></span></td>\r\n			<td><span style=\"font-size:14px\"><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/4_1613702012.PNG\" style=\"height:191px; width:350px\" /></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:center\">\r\n			<p style=\"text-align:left\"><span style=\"font-size:14px\">Workshop 1 Penyusunan Business Plan dan BMC<br />\r\n			Gumaya Tower Hotel, Semarang, 02 - 04 Mei 2019</span></p>\r\n\r\n			<p style=\"text-align:left\">&nbsp;</p>\r\n			</td>\r\n			<td><span style=\"font-size:14px\">Pendampingan Penyusunan Business Plan dan BMC<br />\r\n			Universitas Negeri Semarang, 12 - 13 Juni 2019</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/5_1613703814.PNG\" style=\"height:263px; width:350px\" /></td>\r\n			<td><img alt=\"\" src=\"http://lppm.itk.ac.id/public/uploads/posts/6_1613704471.PNG\" style=\"height:181px; width:350px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Workshop 2 Rekrutmen Tenant dan FGD<br />\r\n			Palembang 19 - 20 Juni 2019</td>\r\n			<td><span style=\"font-size:14px\">Pendampingan 2 Rekrutmen Tenant dan FGD<br />\r\n			Universitas Mataram, 29 - 31 Juli 2019</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><img al